INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (2, 'Morgan', 'Freeman', 'With an authoritative voice and calm demeanor, this ever popular American actor has grown into one of the most respected figures in modern US cinema. ');
INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (3, 'Bob', 'Gunton', 'Bob Gunton is an American actor, primarily known for portraying strict and authoritarian characters in popular films.');
INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (4, 'Marlon', 'Brando', 'Marlon Brando is widely considered the greatest movie actor of all time, rivaled only by the more theatrically oriented Laurence Olivier in terms of esteem. ');
INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (5, 'Al', 'Pacino', 'Alfredo James "Al" ''Pacino established himself as a film actor during one of cinema''s most vibrant decades, the 1970s, and has become an enduring and iconic figure in the world of American movies.');
INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (6, 'Christian', 'Bale', 'Christian Charles Philip Bale was born in Pembrokeshire, Wales, UK on January 30, 1974, to English parents Jennifer "Jenny" (James) and David Bale. ');
INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (7, 'Heath', 'Ledger', 'When hunky, twenty-year-old heart-throb Heath Ledger first came to the attention of the public in 1999, it was all too easy to tag him as a "pretty boy" and an actor of little depth. ');
