INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (9, 'Michael', 'Caine', 'Michael Caine was born Maurice Joseph Micklewhite in London, to Ellen Frances Marie (Burchell), a charlady, and Maurice Joseph Micklewhite, a fish-market porter. ');
