INSERT INTO "actor" ("id", "first_name", "last_name", "info") VALUES (11, 'Martin', 'Balsam', 'Martin Henry Balsam was born on November 4, 1919 in the Bronx, New York City, to Lillian (Weinstein) and Albert Balsam, a manufacturer of women''s sportswear. ');
