INSERT INTO "director" ("id", "first_name", "last_name", "info") VALUES (3, 'Christopher', 'Nolan', 'Best known for his cerebral, often nonlinear, storytelling, acclaimed writer-director Christopher Nolan was born on July 30, 1970, in London, England. ');
INSERT INTO "director" ("id", "first_name", "last_name", "info") VALUES (4, 'Sidney', 'Lumet', 'Sidney Lumet was a master of cinema, best known for his technical knowledge and his skill at getting first-rate performances from his actors -- and for shooting most of his films in his beloved New York. ');
INSERT INTO "director" ("id", "first_name", "last_name", "info") VALUES (5, 'Steven', 'Spielberg', 'One of the most influential personalities in the history of cinema, Steven Spielberg is Hollywood''s best known director and one of the wealthiest filmmakers in the world.');
