INSERT INTO "movie" ("id", "title", "url", "genre", "studio", "director", "date_produced", "total_rating_count", "total_rating", "available", "summary") VALUES (3, 'The Dark Knight', 'https://www.imdb.com/video/imdb/vi324468761?playlistId=tt0468569&ref_=tt_ov_vi', 'Action', 'Warner Bros.', 3, '2008-07-18', NULL, NULL, 't', 'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological and physical tests of his ability to fight injustice.');
INSERT INTO "movie" ("id", "title", "url", "genre", "studio", "director", "date_produced", "total_rating_count", "total_rating", "available", "summary") VALUES (4, '12 Angry Men', 'https://www.imdb.com/video/imdb/vi3452609817?playlistId=tt0050083&ref_=tt_ov_vi', 'Crime', 'Metro-Goldwyn-Mayer Pictures', 4, '1957-04-10', NULL, NULL, 't', 'A jury holdout attempts to prevent a miscarriage of justice by forcing his colleagues to reconsider the evidence.');
INSERT INTO "movie" ("id", "title", "url", "genre", "studio", "director", "date_produced", "total_rating_count", "total_rating", "available", "summary") VALUES (5, 'Schindler''s List', 'https://www.imdb.com/video/imdb/vi1158527769?playlistId=tt0108052&ref_=tt_pr_ov_vi', 'Historical', 'Universal Pictures', 5, '1994-02-04', NULL, NULL, 't', 'In German-occupied Poland during World War II, industrialist Oskar Schindler gradually becomes concerned for his Jewish workforce after witnessing their persecution by the Nazis.');
INSERT INTO "movie" ("id", "title", "url", "genre", "studio", "director", "date_produced", "total_rating_count", "total_rating", "available", "summary") VALUES (6, 'The Lord of the Rings: The Return of the King', 'https://www.imdb.com/video/imdb/vi2073101337?playlistId=tt0167260&ref_=tt_pr_ov_vi', 'Fantasy', 'New Line Cinema', 6, '2003-12-17', NULL, NULL, 't', 'Gandalf and Aragorn lead the World of Men against Sauron''s army to draw his gaze from Frodo and Sam as they approach Mount Doom with the One Ring.');
